    });


    return  <div className="App">
      <input 
      className='search-box' 
      type='search' 
      placeholder=' search monsters' 
      onChange={(event) => {
        const searchString = event.target.value.toLocaleLowerCase()
        this.setState(() => {
          return {searchField:filteredMonsters}
        })
      }}
      />
      {this.state.monsters.map((monster) => { 
          return (
          <div key= {monster.id}>
            <h1>{monster.name}</h1>
          </div>
          )
        })
      }
    </div>
}

}


export default App;
